import React, { useState } from "react";

const Header = ({ text }) => {
	return <h1>{text}</h1>;
};

const Button = ({ clickHandler, text }) => {
	return <button onClick={clickHandler}>{text}</button>;
};

const Statistics = ({ feedback, count }) => {
	return <p>{feedback}: count</p>;
};

const App = () => {
	const [good, setGood] = useState(0);
	const [neutral, setNeutral] = useState(0);
	const [bad, setBad] = useState(0);

	const handleGood = () => setGood(good + 1);
	const handleNeutral = () => setNeutral(neutral + 1);
	const handleBad = () => setBad(bad + 1);

	return (
		<div>
			<Header text="Give Feedback:" />
			<Button clickHandler={handleGood} text="Good" />
			<Button clickHandler={handleNeutral} text="Neutral" />
			<Button clickHandler={handleBad} text="Bad" />
			<Header text="Statistics:" />
			<Statistics feedback="Good" count={good} />
			<Statistics feedback="Neutral" count={neutral} />
			<Statistics feedback="Bad" count={bad} />
		</div>
	);
};

export default App;
